var assign = require('fs-extra/lib/util/assign')

var fse = {}
var gfs = require('graceful-fs')

// attach fs methods to fse
Object.keys(gfs).forEach(function (key) {
  fse[key] = gfs[key]
})

var fs = fse

assign(fs, require('fs-extra/lib/copy'))
assign(fs, require('fs-extra/lib/copy-sync'))
assign(fs, require('fs-extra/lib/mkdirs'))
assign(fs, require('fs-extra/lib/remove'))
assign(fs, require('fs-extra/lib/json'))
assign(fs, require('fs-extra/lib/move'))
assign(fs, require('fs-extra/lib/empty'))
assign(fs, require('fs-extra/lib/ensure'))
assign(fs, require('fs-extra/lib/output'))
assign(fs, require('fs-extra/lib/walk'))
assign(fs, require('fs-extra/lib/walk-sync'))

module.exports = fs

// maintain backwards compatibility for awhile
var jsonfile = {}
Object.defineProperty(jsonfile, 'spaces', {
  get: function () {
    return fs.spaces // found in ./json
  },
  set: function (val) {
    fs.spaces = val
  }
})

module.exports.jsonfile = jsonfile // so users of fs-extra can modify jsonFile.spaces
